    def ctrlCreate(self, ctrName):
        sels = cmds.ls(sl=1)
        ctrColor = cmds.palettePort(self.colorPaletteControl, q=1, scc=1) + 1
        if ctrColor > 24:
            ctrColor -= 1
        operation = cmds.radioCollection(self.collection, q=1, sl=1)
        if operation == self.rb1[self.rb1.rindex('|') + 1:]:
            ctr = cmds.createNode('transform', n=ctrName)
            add = self.addShape(ctr, ctrName[:ctrName.index('_Ctr')], ctrColor)
            cmds.select(cmds.listRelatives(add, p=1))
        elif operation == self.rb2[self.rb2.rindex('|') + 1:]:
            if len(cmds.ls(sl=1)) == 0:
                cmds.error('select something u motherFucker!!!')
            else:
                for sel in cmds.ls(sl=1):
                    add = self.addShape(sel, ctrName[:ctrName.index('_Ctr')], ctrColor)

                cmds.select(cmds.listRelatives(add, p=1))
            cmds.select(sels)
        elif operation == self.rb3[self.rb3.rindex('|') + 1:]:
            if len(cmds.ls(sl=1)) == 0:
                cmds.error("select something u son of monkey's uncle!!!")
            else:
                for sel in cmds.ls(sl=1):
                    selShapes = cmds.listRelatives(sel, f=1, s=1)
                    for shape in selShapes:
                        if cmds.objectType(shape) == 'nurbsCurve':
                            cmds.delete(shape)

                    add = self.addShape(sel, ctrName[:ctrName.index('_Ctr')], ctrColor)

                cmds.select(cmds.listRelatives(add, p=1))
            cmds.select(sels)

    def initPalette(self, Palette):
        if cmds.palettePort(Palette, ex=1):
            for i in range(1, 32):
                tmp = cmds.colorIndex(i, q=1)
                if i < 24:
                    cmds.palettePort(Palette, e=1, r=1, rgb=(i - 1,
                     tmp[0],
                     tmp[1],
                     tmp[2]))
                else:
                    cmds.palettePort(Palette, e=1, r=1, rgb=(i,
                     tmp[0],
                     tmp[1],
                     tmp[2]))

    def colorOverRide(self):
        ctrColor = cmds.palettePort(self.colorPaletteControl, q=1, scc=1) + 1
        if ctrColor > 23:
            ctrColor -= 1
        if ctrColor == 23:
            ctrColor = 18
        sel = cmds.ls(sl=1)
        if len(sel):
            for obj in sel:
                objShapes = cmds.listRelatives(obj, f=1, s=1)
                if objShapes == None:
                    cmds.setAttr(obj + '.ove', 1)
                    cmds.setAttr(obj + '.ovc', ctrColor)
                else:
                    if cmds.objectType(obj) != 'transform':
                        cmds.setAttr(obj + '.ove', 1)
                        cmds.setAttr(obj + '.ovc', ctrColor)
                    for shape in objShapes:
                        cmds.setAttr(shape + '.ove', 1)
                        cmds.setAttr(shape + '.ovc', ctrColor)

        else:
            cmds.error('select nothing u dick head!!!')
        print (ctrColor)
        return

    def expend(self, num):
        if num == 1:
            cmds.window('ctrMakerUI', e=1, h=485)
            cmds.frameLayout('colorFrame', e=1, h=100)
        elif num == 2:
            cmds.window('ctrMakerUI', e=1, h=413)
            cmds.frameLayout('colorFrame', e=1, h=20)

    def createNode1(self, nodeName):
        prefixName = cmds.textField(self.textF, q=1, tx=1)
        if prefixName != '':
            prefixName += '_'
        if nodeName == 'blendNode':
            cmds.shadingNode('blendColors', n=prefixName + 'blendColors', au=1)
        elif nodeName == 'conditionNode':
            cmds.shadingNode('condition', n=prefixName + 'condition', au=1)
        elif nodeName == 'curveInfoNode':
            cmds.shadingNode('curveInfo', n=prefixName + 'curveInfo', au=1)
        elif nodeName == 'disNode':
            cmds.shadingNode('distanceBetween', n=prefixName + 'distanceBetween', au=1)
        elif nodeName == 'mulDivNode':
            cmds.shadingNode('multiplyDivide', n=prefixName + 'multiplyDivide', au=1)
        elif nodeName == 'rangeNode':
            cmds.shadingNode('setRange', n=prefixName + 'setRange', au=1)
        elif nodeName == 'remapValueNode':
            cmds.shadingNode('remapValue', n=prefixName + 'remapValue', au=1)
        elif nodeName == 'reverseNode':
            cmds.shadingNode('reverse', n=prefixName + 'reverse', au=1)

    def win(self):
        ctrlShapePicturePath = self.filePath()
        if cmds.window('ctrMakerUI', ex=1):
            cmds.deleteUI('ctrMakerUI')
        if cmds.windowPref('ctrMakerUI', ex=1):
            cmds.windowPref('ctrMakerUI', r=1)
        cmds.window('ctrMakerUI', title='Control Maker')
        mainForm = cmds.formLayout(w=520, h=485)
        tabs = cmds.tabLayout()
        child1 = cmds.columnLayout(adj=1)
        cmds.rowLayout(w=515, h=30, nc=3, cw3=(80, 80, 80))
        self.collection = cmds.radioCollection()
        self.rb1 = cmds.radioButton(l='Create')
        self.rb2 = cmds.radioButton(l='Add')
        self.rb3 = cmds.radioButton(l='Replace')
        cmds.radioCollection(self.collection, edit=True, select=self.rb1)
        cmds.setParent('..')
        cmds.shelfLayout(w=515, h=330, style='iconOnly', cw=64, ch=64)
        ctrlIconList = cmds.getFileList(fld=ctrlShapePicturePath, fs='*_M.bmp')
        for icon in ctrlIconList:
            iconName = icon[:icon.index('.')]
            ctrName = iconName.replace('icon_M', 'Ctr')
            temp = cmds.shelfButton(c=partial(self.ctrlCreate, ctrName), ann='Create Control')
            imagePath = self.filePath() + '/' + icon
            if cmds.file(imagePath, q=1, ex=1):
                cmds.shelfButton(temp, e=1, i=imagePath)

        cmds.setParent('..')
        cmds.frameLayout('colorFrame', borderStyle='etchedIn', h=100, label='color', cll=1, cc='self.expend(2)', ec='self.expend(1)')
        self.colorPaletteControl = cmds.palettePort(dimensions=(16, 2), ced=0, td=1, at=32, setCurCell=0)
        self.initPalette(self.colorPaletteControl)
        cmds.palettePort(self.colorPaletteControl, e=1, scc=5, h=50)
        ccBtn = cmds.button(label='Change Color', h=26, c=lambda *args: self.colorOverRide())
        cmds.setParent('..')
        cmds.setParent('..')
        child2 = cmds.columnLayout(adj=1)
        cmds.shelfLayout(w=515, h=300, style='iconOnly', cw=85, ch=80)
        nodeIconList = cmds.getFileList(fld=ctrlShapePicturePath, fs='*Node.bmp')
        for icon in nodeIconList:
            iconName = icon[:icon.index('.')]
            temp = cmds.shelfButton(c=partial(self.createNode1, iconName), ann='Create Node')
            imagePath = self.filePath() + '/' + icon
            if cmds.file(imagePath, q=1, ex=1):
                cmds.shelfButton(temp, e=1, i=imagePath)

        cmds.setParent('..')
        cmds.text(h=30, label='PrefixName :')
        self.textF = cmds.textField()
        cmds.textField(self.textF, e=1, h=30, enterCommand='cmds.setFocus("' + self.textF + '")')
        cmds.tabLayout(tabs, e=1, tabLabel=((child1, '   Ctrl   '), (child2, '   Node   ')), selectCommand='cmds.setFocus("' + self.textF + '")')
        cmds.showWindow('ctrMakerUI')

    def whereAmI(self):
        scriptPath = os.path.split(os.path.abspath(__file__))
        datePath = scriptPath[0].replace('\\', '/')
        Path = datePath.replace('', '')
        return Path

    def filePath(self):
        return self.whereAmI() + '/ctrlShapePicture'